import React from "react";
import Modal from "../Modal";
import history from "../../history";

// React.Fragment is used instead of div as we still need to return one element
// but we want at render time that div to not show , so the buttons can sit
// directly in the modal actions area without parent div
class StreamDelete extends React.Component {
  renderActions() {
    const actions = (
      <React.Fragment>
        <button className="ui button negative">Delete</button>
        <button className="ui button">Cancel</button>
      </React.Fragment>
    );
  }

  render() {
    return (
      <div>
        StreamDelete
        <Modal
          title="Delete Stream"
          content="Are you sure you want to delete this stream?"
          actions={actions}
          onDismiss={() => {
            history.push("/");
          }}
        ></Modal>
      </div>
    );
  }
}

export default StreamDelete;
