export const authReducer = (state = null, action) => {
  switch (action.type) {
    case "SIGNIN_ACTION":
    case "SIGNOUT_ACTION":
    case "AUTH_STATE_CHANGE":
      return action.payload;
    default:
      return state;
  }
};
